import React, { useState, useRef } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import InputText from "../component/inputText";

export default function Login() {
  const I = { EMAIL: 0, PASSWD: 1 };
  const [inEmail, setInEmail] = useState("");
  const [inPasswd, setInPasswd] = useState("");
  const [visibleGuide, setVisibleGuide] = useState([true, true]);
  const ref = [useRef(null), useRef(null)];
  const navigate = useNavigate();

  function setGuide(idx, msg) {
    if (!isShowGuide(idx)) return null;
    return (
      <div
        className="itemBoxOverlap"
        onClick={(e) => {
          showGuide(idx, false);
        }}
      >
        {msg}
      </div>
    );
  }

  function showGuide(idx, isShow) {
    visibleGuide[idx] = isShow;
    setVisibleGuide([...visibleGuide]);
    if (!isShow) ref[idx].current.focus();
  }

  function isShowGuide(idx) {
    var v = idx === I.EMAIL ? inEmail : inPasswd;
    return !v && visibleGuide[idx] ? true : false;
  }

  function onClickLogin() {
    axios
      .post("http://localhost:8000/login", {
        email: inEmail,
        password: inPasswd,
      })
      .then((res) => {
        if (res.data.token) {
          //          setToken(res.data.token);
          navigate("/profile");
        }
      })
      .catch((error) => {
        console.log(error, "error");
      });
  }

  return (
    <div
      className="contentBoard"
      style={{
        width: "60vw",
        height: "50vh",
        maxWidth: "800px",
        maxHeight: "600px",
      }}
    >
      <div className="contentContainer">
        <h2 className="pageTitle">아주물고기</h2>
        <div style={{ width: "65%" }}>
          <InputText
            name="in_email"
            value={inEmail}
            label="E-mail"
            guide="이메일을 입력하세요."
            onChange={(e) => {
              setInEmail(e.target.value);
            }}
          />
          <InputText
            name="in_passwd"
            value={inPasswd}
            label="Password"
            guide="비밀번호를 입력해주세요."
            onChange={(e) => {
              setInPasswd(e.target.value);
            }}
          />
          <div>
            <button type="button" onClick={onClickLogin} className="button">
              로그인
            </button>
          </div>
        </div>
        <div
          style={{
            width: "100%",
            height: "1.6vw",
            textAlign: "right",
            paddingRight: "5vw",
          }}
        >
          <Link to="/join" className="textLink">
            회원가입하기
          </Link>
        </div>
      </div>
    </div>
  );
}
