import React, { useState, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [inputId, setInputId] = useState("");
  const [inputPw, setInputPw] = useState("");
  const [visibleGuide, setVisibleGuide] = useState([true, true]);
  const ref = [useRef(null), useRef(null)];
  const navigate = useNavigate();

  function showGuide(idx, isShow) {
    visibleGuide[idx] = isShow;
    setVisibleGuide([...visibleGuide]);
    if (!isShow) ref[idx].current.focus();
  }

  function isShowGuide(idx) {
    var v = idx === 0 ? inputId : inputPw;
    return v === "" && visibleGuide[idx] ? true : false;
  }

  function onClickLogin() {
    axios
      .post("http://localhost:8000/login", {
        id: inputId,
        pw: inputPw,
      })
      .then((res) => {
        if (res.data.token) {
          //          setToken(res.data.token);
          navigate("/profile");
        }
      })
      .catch((error) => {
        console.log(error, "error");
      });
  }

  return (
    <div className="contentBoard" style={{ width: "60vw", height: "60vh" }}>
      <div className="contentContainer">
        <h2 className="pageTitle">아주물고기</h2>
        <div className="itemContainer" style={{ border: "solid 1px blue" }}>
          <label htmlFor="input_id" style={{ paddingLeft: "1.3vw" }}>
            E-mail
          </label>
          <br />
          <div className="itemBox">
            {isShowGuide(0) && (
              <div
                className="itemBoxOverlap"
                onClick={(e) => {
                  showGuide(0, false);
                }}
              >
                이메일을 입력해주세요.
              </div>
            )}
            <input
              type="text"
              name="input_id"
              value={inputId}
              ref={ref[0]}
              onChange={(e) => {
                setInputId(e.target.value);
              }}
              onFocus={(e) => {
                showGuide(0, false);
              }}
              onBlur={(e) => {
                showGuide(0, true);
              }}
            />
          </div>
        </div>
        <div className="itemContainer">
          <label htmlFor="input_pw" style={{ paddingLeft: "1.3vw" }}>
            Password
          </label>
          <br />
          <div className="itemBox">
            {isShowGuide(1) && (
              <div
                className="itemBoxOverlap"
                onClick={(e) => {
                  showGuide(1, false);
                }}
              >
                비밀번호를 입력해주세요.
              </div>
            )}
            <input
              type="password"
              name="input_pw"
              value={inputPw}
              ref={ref[1]}
              onChange={(e) => {
                setInputPw(e.target.value);
              }}
              onFocus={(e) => {
                showGuide(1, false);
              }}
              onBlur={(e) => {
                showGuide(1, true);
              }}
            />
          </div>
        </div>

        <div>
          <button type="button" onClick={onClickLogin} className="button">
            로그인
          </button>
        </div>
      </div>
    </div>
  );
}
