import React, { useState, useRef, useEffect } from "react";

export default function  InputText(props) {

    const [show, setShow] = useState(props.guide.length > 0);
    const ref = useRef(null);

    function clickGuide() {
      setShow(false);
      ref.current.focus();
    }

    function setGuide() {
      if (props.value.length > 0 || !show) return null;
      return (
        <div className="itemBoxOverlap" onClick={clickGuide}>
        {props.guide}
        </div>
      )
    }

    return (
      <div className="itemContainer">
        <label htmlFor={props.name} style={{paddingLeft:"1.3vw"}}>{props.label}</label><br/>
        <div className="itemBox">
        <div className="itemBoxOverlap2" align="right">TEST</div>
          { setGuide() }
          <input type="text" name={props.name} value={props.value}
            ref={ref}
            onChange={props.onChange}
            onFocus={(e)=>{ setShow(false); }}
            onBlur={(e)=>{ setShow(true); }}
          />
        </div>
      </div>
    );

};
