import React, { useState, useRef } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

export default function Join() {
  const I = { EMAIL: 0, GRADE: 1, MAJOR: 2, PASSWD: 3, PASSWD2: 4 };
  const [inEmail, setInEmail] = useState("");
  const [inGrade, setInGrade] = useState("");
  const [inMajor, setInMajor] = useState("");
  const [inPasswd, setInPasswd] = useState("");
  const [inPasswd2, setInPasswd2] = useState("");

  const [visibleGuide, setVisibleGuide] = useState([
    true,
    true,
    true,
    true,
    true,
  ]);
  const ref = [
    useRef(null),
    useRef(null),
    useRef(null),
    useRef(null),
    useRef(null),
  ];
  const navigate = useNavigate();

  function setGuide(idx, msg) {
    if (!isShowGuide(idx)) return null;
    return (
      <div
        className="itemBoxOverlap"
        onClick={(e) => {
          showGuide(idx, false);
        }}
      >
        {msg}
      </div>
    );
  }

  function showGuide(idx, isShow) {
    visibleGuide[idx] = isShow;
    setVisibleGuide([...visibleGuide]);
    if (!isShow) ref[idx].current.focus();
  }

  function isShowGuide(idx) {
    var v;
    switch (idx) {
      case I.EMAIL:
        v = inEmail;
        break;
      case I.GRADE:
        v = inGrade;
        break;
      case I.MAJOR:
        v = inMajor;
        break;
      case I.PASSWD:
        v = inPasswd;
        break;
      case I.PASSWD2:
        v = inPasswd2;
        break;
    }
    return !v && visibleGuide[idx] ? true : false;
  }

  function onClickJoin() {
    if (inPasswd !== inPasswd2) {
      alert("비밀번호가 일치하지 않습니다");
      ref[I.PASSWD].current.focus();
    }
    axios
      .post("http://localhost:8000/login", {
        email: inEmail,
        grade: inGrade,
        major: inMajor,
        password: inPasswd,
      })
      .then((res) => {
        if (res.data.token) {
          //          setToken(res.data.token);
          navigate("/profile");
        }
      })
      .catch((error) => {
        console.log(error, "error");
      });
  }

  return (
    <div className="contentBoard" style={{ width: "50vw", height: "90vh" }}>
      <div className="contentContainer">
        <h2 className="pageTitle">아주물고기</h2>
        <div style={{ width: "85%" }}>
          <div className="itemContainer">
            <label htmlFor="in_email" style={{ paddingLeft: "1.3vw" }}>
              E-mail
            </label>
            <br />
            <div className="itemBox">
              {setGuide(I.EMAIL, "이메일을 입력해주세요.")}
              <input
                type="text"
                name="in_email"
                value={inEmail}
                ref={ref[I.EMAIL]}
                onChange={(e) => {
                  setInEmail(e.target.value);
                }}
                onFocus={(e) => {
                  showGuide(I.EMAIL, false);
                }}
                onBlur={(e) => {
                  showGuide(I.EMAIL, true);
                }}
              />
            </div>
          </div>
          <div className="itemContainer">
            <label htmlFor="in_grade" style={{ paddingLeft: "1.3vw" }}>
              grade
            </label>
            <br />
            <div className="itemBox">
              {setGuide(I.GRADE, "학년을 입력해주세요.")}
              <input
                type="text"
                name="in_grade"
                value={inGrade}
                ref={ref[I.GRADE]}
                onChange={(e) => {
                  setInGrade(e.target.value);
                }}
                onFocus={(e) => {
                  showGuide(I.GRADE, false);
                }}
                onBlur={(e) => {
                  showGuide(I.GRADE, true);
                }}
              />
            </div>
          </div>
          <div className="itemContainer">
            <label htmlFor="in_major" style={{ paddingLeft: "1.3vw" }}>
              major
            </label>
            <br />
            <div className="itemBox">
              {setGuide(I.MAJOR, "전공을 입력해주세요.")}
              <input
                type="text"
                name="in_major"
                value={inMajor}
                ref={ref[I.MAJOR]}
                onChange={(e) => {
                  setInMajor(e.target.value);
                }}
                onFocus={(e) => {
                  showGuide(I.MAJOR, false);
                }}
                onBlur={(e) => {
                  showGuide(I.MAJOR, true);
                }}
              />
            </div>
          </div>
          <div className="itemContainer">
            <label htmlFor="in_passwd" style={{ paddingLeft: "1.3vw" }}>
              Password
            </label>
            <br />
            <div className="itemBox">
              {setGuide(I.PASSWD, "비밀번호를 입력해주세요.")}
              <input
                type="password"
                name="in_passwd"
                value={inPasswd}
                ref={ref[I.PASSWD]}
                onChange={(e) => {
                  setInPasswd(e.target.value);
                }}
                onFocus={(e) => {
                  showGuide(I.PASSWD, false);
                }}
                onBlur={(e) => {
                  showGuide(I.PASSWD, true);
                }}
              />
            </div>
          </div>
          <div className="itemContainer">
            <label htmlFor="in_passwd2" style={{ paddingLeft: "1.3vw" }}>
              Password
            </label>
            <br />
            <div className="itemBox">
              {setGuide(I.PASSWD2, "비밀번호를 한번 더 입력해주세요.")}
              <input
                type="password"
                name="in_passwd2"
                value={inPasswd2}
                ref={ref[I.PASSWD2]}
                onChange={(e) => {
                  setInPasswd2(e.target.value);
                }}
                onFocus={(e) => {
                  showGuide(I.PASSWD2, false);
                }}
                onBlur={(e) => {
                  showGuide(I.PASSWD2, true);
                }}
              />
            </div>
          </div>
          <div>
            <button type="button" onClick={onClickJoin} className="button">
              동의하고 가입하기
            </button>
          </div>
          <div
            style={{ width: "100%", textAlign: "center", fontSize: "1.1vw" }}
          >
            이용 약관, 개인정보 수집 및 이용, 개인정보 제공 내용을 확인
            하였으며, 동의합니다.
          </div>
        </div>
      </div>
    </div>
  );
}
