import React, { useEffect } from "react";
import InputText from "../component/InputText";
import axios from "axios";
import GlobalVar from "../const/GlobalVar";

export default function Notice() {
  let list = [
    {
      date: "2023-12-13",
      title: "[학부]2023-2학기 수강포기(수강철회)",
      from: "자연과학대학",
    },
    {
      date: "2023-12-13",
      title: "[학사] 2023-2학기 휴학/복학 신청 안내",
      from: "수학과",
    },
  ];
  let returnElement = [];

  list.forEach((e) => {
    returnElement.push(
      <div style={{ height: "20%" }}>
        <div className="notice">
          <header>
            <nav>
              {e["date"]} {e["from"]}
            </nav>
          </header>
          <main>{e["title"]}</main>
        </div>
      </div>
    );
  });

  useEffect(() => {
    const apiCall = async () => {
      const response = await axios.get(GlobalVar.API_URL);
      console.log(response.data);
    };
    apiCall();
  }, []);

  return (
    <div
      style={{ width: "100%", height: "95%", padding: "20px 50px 20px 50px" }}
    >
      <div className="leftBoard">
        <div className="search">
          <InputText
            name="search"
            autoFocus
            maxLength="40"
            tabIndex="1"
            placeholder="검색어를 입력해주세요."
          />
        </div>
        <div className="search">
          <h3>hashtag</h3>
        </div>
        <div className="noticeList">{returnElement}</div>
      </div>
      <div className="rightBoard">
        <iframe
          title="noticeInfo"
          className="rightIframe"
          src="https://www.ajou.ac.kr/kr/ajou/notice.do?mode=view&articleNo=226932&article.offset=0&articleLimit=10"
        />
      </div>
    </div>
  );
}
