import React, { useState, useRef } from "react";

export default function InputText(props) {
  const [show, setShow] = useState(props.guide !== "");
  const ref = useRef(null);

  function clickGuide() {
    setShow(false);
    ref.current.focus();
  }

  function setGuide() {
    if (props.value !== "" || !show) return null;
    return (
      <div className="itemBoxOverlap" onClick={clickGuide}>
        {props.guide}
      </div>
    );
  }

  return (
    <div className="itemContainer">
      <label htmlFor={props.name} style={{ paddingLeft: "10px" }}>
        {props.label}
      </label>
      <br />
      <div className="itemBox">
        {setGuide()}
        <input
          type="text"
          name={props.name}
          value={props.value}
          ref={ref}
          onChange={props.onChange}
          onFocus={(e) => {
            setShow(false);
          }}
          onBlur={(e) => {
            setShow(true);
          }}
        />
      </div>
    </div>
  );
}
